function getImage(){
  //画像ファイル名を配列で保持
  var arr = ['https://blog.cnobi.jp/v1/blog/user/90697e8e8cf70c76e3dff5535cdb521f/1695448780', 'https://blog.cnobi.jp/v1/blog/user/90697e8e8cf70c76e3dff5535cdb521f/1695448779', 'https://blog.cnobi.jp/v1/blog/user/90697e8e8cf70c76e3dff5535cdb521f/1695448778'];

  //ランダムで画像ファイルを取得して表示する
  var obj = document.getElementById("im1");
  var a = Math.floor(Math.random() * arr.length);
  obj.src = arr[a];
}
